document.addEventListener("DOMContentLoaded",()=>{const e=document.createElement("button");e.className="theme-toggle",e.innerHTML='<i class="fas fa-moon"></i>',document.body.appendChild(e),localStorage.getItem("theme")==="dark"&&(document.body.classList.add("dark-theme"),e.innerHTML='<i class="fas fa-sun"></i>'),e.addEventListener("click",()=>{document.body.classList.toggle("dark-theme"),document.body.classList.contains("dark-theme")?(localStorage.setItem("theme","dark"),e.innerHTML='<i class="fas fa-sun"></i>'):(localStorage.setItem("theme","light"),e.innerHTML='<i class="fas fa-moon"></i>')})})